var OriginTitile=document.title;
var titleTime;document.addEventListener("visibilitychange",
    function()
    {
        if(document.hidden)
        {
            document.title="有本事别走!∑(ﾟДﾟノ)ノ ";
            clearTimeout(titleTime)
        }
    else
    {
        document.title="哈哈，真听话！ヾ(ﾟ∀ﾟゞ)";
        titleTime=setTimeout(function(){document.title=OriginTitile},2000)
    }
}
);
